define('node_modules/egis-load-css/load-css', function(require, exports, module) {

  
  var cookie=require('node_modules/egis-cookie/cookie');
  
  function loadCss(src,fn){
      var node=document.createElement('link');
      node.rel='stylesheet';
      node.href=src;
  
      if(!isLoaded(src)){
          document.head.insertBefore(node,document.head.lastChild);
      }else{
          fn(null,node);
      }
  
      if(node.attachEvent){
          node.attachEvent('onload', function(){fn(null,node)});
      }else{
          setTimeout(function() {
              poll(node, fn);
          }, 0); // for cache
      }
      function poll(node,callback){
          var isLoaded = false;
          if(/webkit/i.test(navigator.userAgent)) {//webkit
              if (node['sheet']) {
                  isLoaded = true;
              }
          }else if(node['sheet']){// for Firefox
              try{
                  if (node['sheet'].cssRules) {
                      isLoaded = true;
                  }
              }catch(ex){
                  // NS_ERROR_DOM_SECURITY_ERR
                  if (ex.code === 1000) {
                      isLoaded = true;
                  }
              }
          }
          if(isLoaded){
              setTimeout(function(){
                  callback(null,node);
              },1);
          }else{
              setTimeout(function(){
                  poll(node,callback);
              },10);
          }
      }
      function isLoaded(src){
          var isLoaded=false;
          var dom_links=document.head.getElementsByTagName("link");
          for(var i=0;i<dom_links.length;i++){
              var dom_link=dom_links[i];
              var dom_link_src=dom_link.getAttribute('href');
              if(src==dom_link_src){
                  isLoaded=true;
                  break;
              }
          }
          return isLoaded;
      }
      node.onLoad=function(){
          fn(null,node);
      }
  }
  module.exports=loadCss;

});
